import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);
  const [pointsAwarded, setPointsAwarded] = useState(null);
  const [newUserName, setNewUserName] = useState('');

  const fetchUsers = async () => {
    const res = await axios.get('http://localhost:5000/api/users');
    setUsers(res.data);
  };

  const fetchLeaderboard = async () => {
    const res = await axios.get('http://localhost:5000/api/users/leaderboard');
    setLeaderboard(res.data);
  };

  const claimPoints = async () => {
    if (!selectedUser) return alert("Please select a user");
    const res = await axios.post(`http://localhost:5000/api/users/claim/${selectedUser}`);
    setPointsAwarded(res.data.points);
    fetchUsers();
    fetchLeaderboard();
  };

  const addUser = async () => {
    if (!newUserName.trim()) return;
    await axios.post('http://localhost:5000/api/users', { name: newUserName });
    setNewUserName('');
    fetchUsers();
    fetchLeaderboard();
  };

  useEffect(() => {
    fetchUsers();
    fetchLeaderboard();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üèÜ Leaderboard System</h1>

      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          value={newUserName}
          placeholder="Enter new user name"
          onChange={(e) => setNewUserName(e.target.value)}
        />
        <button onClick={addUser}>Add User</button>
      </div>

      <div>
        <select onChange={(e) => setSelectedUser(e.target.value)}>
          <option value="">Select a user</option>
          {users.map(user => (
            <option key={user._id} value={user._id}>{user.name}</option>
          ))}
        </select>
        <button onClick={claimPoints}>Claim Points</button>
        {pointsAwarded && <p>üéØ {pointsAwarded} points awarded!</p>}
      </div>

      <h2>üìä Leaderboard</h2>
      <ol>
        {leaderboard.map((user, index) => (
          <li key={user._id}>
            {index + 1}. {user.name} - {user.totalPoints} pts
          </li>
        ))}
      </ol>
    </div>
  );
}

export default App;
