import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);
  const [pointsAwarded, setPointsAwarded] = useState(null);
  const [newUserName, setNewUserName] = useState('');
  const [newUserAvatar, setNewUserAvatar] = useState('');

  const fetchUsers = async () => {
    const res = await axios.get('http://localhost:5000/api/users');
    setUsers(res.data);
  };

  const fetchLeaderboard = async () => {
    const res = await axios.get('http://localhost:5000/api/users/leaderboard');
    setLeaderboard(res.data);
  };

  const claimPoints = async () => {
    if (!selectedUser) return alert("Please select a user");
    const res = await axios.post(`http://localhost:5000/api/users/claim/${selectedUser}`);
    setPointsAwarded(res.data.points);
    fetchUsers();
    fetchLeaderboard();
  };

  const addUser = async () => {
    if (!newUserName.trim()) return;
    await axios.post('http://localhost:5000/api/users', { name: newUserName, avatarUrl: newUserAvatar });
    setNewUserName('');
    setNewUserAvatar('');
    fetchUsers();
    fetchLeaderboard();
  };

  useEffect(() => {
    fetchUsers();
    fetchLeaderboard();
  }, []);

  return (
    <div className="app-container">
      <h1>🏆 Monthly Wealth Ranking</h1>

      <div className="new-user-form">
        <input type="text" value={newUserName} placeholder="User Name" onChange={(e) => setNewUserName(e.target.value)} />
        <input type="text" value={newUserAvatar} placeholder="Avatar URL" onChange={(e) => setNewUserAvatar(e.target.value)} />
        <button onClick={addUser}>Add User</button>
      </div>

      <div className="claim-section">
        <select onChange={(e) => setSelectedUser(e.target.value)}>
          <option value="">Select a user</option>
          {users.map(user => (
            <option key={user._id} value={user._id}>{user.name}</option>
          ))}
        </select>
        <button onClick={claimPoints}>Claim Points</button>
        {pointsAwarded && <p className="points-msg">🎯 {pointsAwarded} points awarded!</p>}
      </div>

      <div className="leaderboard">
        <div className="top-three">
          {leaderboard.slice(0, 3).map((user, index) => (
            <div key={user._id} className={`top-user rank-${index + 1}`}>
              <img src={user.avatarUrl || 'https://via.placeholder.com/80'} alt={user.name} />
              <p>{user.name}</p>
              <span>{user.totalPoints} pts</span>
            </div>
          ))}
        </div>

        <div className="others">
          {leaderboard.slice(3, 10).map((user, index) => (
            <div key={user._id} className="user-row">
              <span>#{index + 4}</span>
              <img src={user.avatarUrl || 'https://via.placeholder.com/40'} alt={user.name} />
              <span>{user.name}</span>
              <span>{user.totalPoints} pts</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
