import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

// Importing icons (you'd typically use an icon library like Font Awesome or simply SVGs)
// For simplicity, I'll use placeholders or simple text.



function App() {
    const [leaderboard, setLeaderboard] = useState([]);
    const [settlementTime, setSettlementTime] = useState('14 days 01:45:47'); // Static for now, can be dynamic
    const [currentUserRank, setCurrentUserRank] = useState({ name: 'Devil', totalPoints: 0 }); // Placeholder for the bottom user

    const fetchLeaderboard = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/users/leaderboard');
            setLeaderboard(res.data);
            // Simulate the current user's rank at the bottom if not in top 6
            const devilUser = res.data.find(user => user.name === 'Devil'); // Find a specific user
            if (devilUser) {
                setCurrentUserRank(devilUser);
            }
        } catch (error) {
            console.error('Error fetching leaderboard:', error);
        }
    };

    useEffect(() => {
        fetchLeaderboard();
        // Optional: Update settlement time every second
        // const interval = setInterval(() => {
        //     // Logic to decrement settlement time
        // }, 1000);
        // return () => clearInterval(interval);
    }, []);

    // Placeholder images for avatars and icons
    const getAvatar = (user) => user.avatarUrl || 'https://via.placeholder.com/60';
    const getMysteryBillionaireAvatar = () => 'https://via.placeholder.com/80?text=MB'; // Or your actual image

    return (
        <div className="app-container">
            {/* Top Navigation */}
            <div className="top-nav">
                <div className="nav-left">
                    <span className="back-arrow">‚Üê</span>
                    <span className="device-status">1 device</span>
                </div>
                <div className="nav-center">
                    <span className="nav-item">e Ranking</span>
                    <span className="nav-item">Hourly Ranking</span>
                    <span className="nav-item">Family Ranking</span>
                    <span className="nav-item active">Wealth Ranking</span>
                </div>
                <div className="nav-right">
                    <QuestionMarkIcon className="question-icon" />
                </div>
            </div>

            {/* Daily/Monthly Tabs (Simplified) */}
            <div className="ranking-tabs">
                <span className="tab-item">Daily</span>
                <span className="tab-item active">Monthly</span>
            </div>

            {/* Settlement and Rewards */}
            <div className="settlement-rewards">
                <p className="settlement-time">Settlement time {settlementTime}</p>
                <div className="rewards-box">
                    <GiftBoxIcon className="gift-icon" />
                    <span>Rewards</span>
                </div>
            </div>

            {/* Top 3 Section */}
            <div className="top-three-ranking">
                <div className="rank-item rank-2">
                    {leaderboard[1] && (
                        <>
                            <img src={getAvatar(leaderboard[1])} alt={leaderboard[1].name} className="avatar" />
                            <p className="user-name">{leaderboard[1].name}</p>
                            <div className="points-display">
                                <GoldCoinIcon className="coin-icon" />
                                <span>{String(leaderboard[1].totalPoints).slice(0, 1)}*****{String(leaderboard[1].totalPoints).slice(-1)}</span>
                            </div>
                        </>
                    )}
                </div>
                <div className="rank-item rank-1">
                    {leaderboard[0] && (
                        <>
                            <div className="crown-icon">üëë</div> {/* Placeholder for crown */}
                            <img src={getAvatar(leaderboard[0])} alt={leaderboard[0].name} className="avatar" />
                            <p className="user-name">{leaderboard[0].name}</p>
                            <div className="points-display">
                                <GoldCoinIcon className="coin-icon" />
                                <span>{String(leaderboard[0].totalPoints).slice(0, 1)}*****{String(leaderboard[0].totalPoints).slice(-1)}</span>
                            </div>
                        </>
                    )}
                </div>
                <div className="rank-item rank-3">
                    {leaderboard[2] && (
                        <>
                            <img src={getAvatar(leaderboard[2])} alt={leaderboard[2].name} className="avatar" />
                            <p className="user-name">{leaderboard[2].name}</p>
                            <div className="points-display">
                                <GoldCoinIcon className="coin-icon" />
                                <span>{String(leaderboard[2].totalPoints).slice(0, 1)}*****{String(leaderboard[2].totalPoints).slice(-1)}</span>
                            </div>
                        </>
                    )}
                </div>
            </div>

            {/* Other Rankings */}
            <div className="other-rankings">
                {leaderboard.slice(3, 6).map((user, index) => (
                    <div className="ranking-row" key={user._id}>
                        <span className="rank-number">{index + 4}</span>
                        <img src={getAvatar(user)} alt={user.name} className="row-avatar" />
                        <span className="row-name">{user.name}</span>
                        <div className="row-points">
                            <span>{String(user.totalPoints).slice(0, 1)}****{String(user.totalPoints).slice(-1)}</span>
                            <GoldCoinIcon className="row-coin-icon" />
                        </div>
                    </div>
                ))}
            </div>

            {/* Current User at Bottom */}
            <div className="current-user-rank">
                <span className="rank-number">999+</span>
                <img src={getAvatar(currentUserRank)} alt={currentUserRank.name} className="row-avatar" />
                <span className="row-name">{currentUserRank.name}</span>
                <div className="row-points">
                    <span>{currentUserRank.totalPoints}</span>
                    <GoldCoinIcon className="row-coin-icon" />
                </div>
            </div>

            {/* Remove existing input fields and buttons for adding/claiming users as they are not in the image */}
            {/* You can keep them for development/testing if needed, but they won't be styled like the image */}
            {/*
            <div className="new-user-form">
                <input type="text" value={newUserName} placeholder="User Name" onChange={(e) => setNewUserName(e.target.value)} />
                <input type="text" value={newUserAvatar} placeholder="Avatar URL" onChange={(e) => setNewUserAvatar(e.target.value)} />
                <button onClick={addUser}>Add User</button>
            </div>

            <div className="claim-section">
                <select onChange={(e) => setSelectedUser(e.target.value)}>
                    <option value="">Select a user</option>
                    {users.map(user => (
                        <option key={user._id} value={user._id}>{user.name}</option>
                    ))}
                </select>
                <button onClick={claimPoints}>Claim Points</button>
                {pointsAwarded && <p className="points-msg">üéØ {pointsAwarded} points awarded!</p>}
            </div>
            */}
        </div>
    );
}

export default App;
