import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import NewUserForm from './components/NewUserForm';
import ClaimPoints from './components/ClaimPoints';
import TopThree from './components/TopThree';
import LeaderboardList from './components/LeaderboardList';

function App() {
  const [users, setUsers] = useState([]);
  const [leaderboard, setLeaderboard] = useState([]);
  const [pointsAwarded, setPointsAwarded] = useState(null);

  const fetchUsers = async () => {
    const res = await axios.get('http://localhost:5000/api/users');
    setUsers(res.data);
  };

  const fetchLeaderboard = async () => {
    const res = await axios.get('http://localhost:5000/api/users/leaderboard');
    setLeaderboard(res.data);
  };

  useEffect(() => {
    fetchUsers();
    fetchLeaderboard();
  }, []);

  return (
    <div className="app-container">
      <h1>üèÜ Wealth Ranking</h1>

      <NewUserForm onUpload={fetchUsers} refreshLeaderboard={fetchLeaderboard} />
      <ClaimPoints
        users={users}
        onClaim={fetchUsers}
        refreshLeaderboard={fetchLeaderboard}
        pointsAwarded={pointsAwarded}
        setPointsAwarded={setPointsAwarded}
      />

      <div className="leaderboard">
        <TopThree leaderboard={leaderboard} />
        <LeaderboardList leaderboard={leaderboard.slice(3)} />
      </div>
    </div>
  );
}

export default App;
