import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);
  const [pointsAwarded, setPointsAwarded] = useState(null);

  const fetchUsers = async () => {
    const res = await axios.get('http://localhost:5000/api/users');
    setUsers(res.data);
  };

  const fetchLeaderboard = async () => {
    const res = await axios.get('http://localhost:5000/api/users/leaderboard');
    setLeaderboard(res.data);
  };

  const claimPoints = async () => {
    if (!selectedUser) return alert("Select a user!");
    const res = await axios.post(`http://localhost:5000/api/users/claim/${selectedUser}`);
    setPointsAwarded(res.data.points);
    fetchUsers();
    fetchLeaderboard();
  };

  useEffect(() => {
    fetchUsers();
    fetchLeaderboard();
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>üèÜ Leaderboard</h1>

      <select onChange={(e) => setSelectedUser(e.target.value)}>
        <option value="">-- Select User --</option>
        {users.map(u => (
          <option key={u._id} value={u._id}>{u.name}</option>
        ))}
      </select>
      <button onClick={claimPoints}>Claim</button>

      {pointsAwarded && <p>üéØ You got {pointsAwarded} points!</p>}

      <h2>üìà Rankings</h2>
      <ol>
        {leaderboard.map((u, i) => (
          <li key={u._id}>
            {u.name} - {u.totalPoints} pts
          </li>
        ))}
      </ol>
    </div>
  );
}

export default App;
